mapper.select:
select m.perms from sys_menu m left join sys_role_menu rm on rm.menu_id = m.menu_id left join sys_user_role ur on ur.role_id = rm.role_id
and ur.user_id = #{userId}
and m.perms is not null

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, visible = #{visible}, status = #{status}, perms = #{perms}, icon = #{icon}, create_by = #{createBy}, create_name = #{createName}, create_date = #{createDate}, update_by = #{updateBy}, update_name = #{updateName}, update_date = #{updateDate}, remark = #{remark},
where menu_id = #{menuId}
