mapper.select:

left join sys_user_dept ud on ud.user_id=u.user_id
left join sys_dept d on d.dept_id=ud.dept_id


        and u.user_id = #{userId}


        and u.username = #{username}


        and u.nickname = #{nickname}


        and u.birthday = #{birthday}


        and u.sex = #{sex}


        and u.id_card = #{idCard}


        and u.phone_number = #{phoneNumber}


        and u.email = #{email}


        and u.fax = #{fax}


        and u.status = #{status}


        and u.del_flag = #{delFlag}


        and u.login_ip = #{loginIp}


        and u.login_date = #{loginDate}


        and d.dept_id = #{deptId}

mapper.update:
update sys_user
username = #{username}, password = #{password}, nickname = #{nickname}, birthday = #{birthday}, sex = #{sex}, id_card = #{idCard}, phone_number = #{phoneNumber}, email = #{email}, fax = #{fax}, avatar = #{avatar}, status = #{status}, del_flag = #{delFlag}, login_ip = #{loginIp}, login_date = #{loginDate}, create_by = #{createBy}, create_name = #{createName}, create_date = #{createDate}, update_by = #{updateBy}, update_name = #{updateName}, update_date = #{updateDate}, remark = #{remark},
where user_id = #{userId}
