mapper.select:
select dict_value "dictValue", dict_label "dictLabel" from sys_dict_data


        and dict_code = #{dictCode}


        and dict_sort = #{dictSort}


        and dict_label = #{dictLabel}


        and dict_value = #{dictValue}


        and dict_type = #{dictType}


        and status = #{status}


        and create_by = #{createBy}


        and create_name = #{createName}


        and create_date = #{createDate}


        and update_by = #{updateBy}


        and update_name = #{updateName}


        and update_date = #{updateDate}


order by dict_sort

select count(1)
from sys_dict_data


        and dict_code = #{dictCode}


        and dict_sort = #{dictSort}


        and dict_label = #{dictLabel}


        and dict_value = #{dictValue}


        and dict_type = #{dictType}


        and status = #{status}


        and create_by = #{createBy}


        and create_name = #{createName}


        and create_date = #{createDate}


        and update_by = #{updateBy}


        and update_name = #{updateName}


        and update_date = #{updateDate}

mapper.update:
update sys_dict_data
dict_sort = #{dictSort}, dict_label = #{dictLabel}, dict_value = #{dictValue}, dict_type = #{dictType}, status = #{status}, create_by = #{createBy}, create_name = #{createName}, create_date = #{createDate}, update_by = #{updateBy}, update_name = #{updateName}, update_date = #{updateDate},
where dict_code = #{dictCode}
